create table comment
(
    comment_id            int unsigned auto_increment comment '评论ID'
        primary key,
    comment_article_id    int unsigned                 not null comment '文章ID',
    comment_author_name   varchar(50) charset utf8mb3  null comment '评论人名称',
    comment_author_email  varchar(50) charset utf8mb3  null comment '评论人邮箱',
    comment_author_avatar varchar(100) charset utf8mb3 null comment '评论人头像',
    comment_content       text charset utf8mb3         null comment '内容',
    comment_ip            varchar(50) charset utf8mb3  null comment 'IP',
    comment_create_time   datetime                     null comment '评论时间',
    comment_role          int                          null comment '角色，是否管理员/是否是友人',
    comment_level         int                          null comment '评论等级',
    comment_update_time   datetime                     null comment '更新时间',
    status                int                          null comment '状态',
    is_delete             int                          null comment '是否删除'
)
    row_format = DYNAMIC;

